  /**
   * Metadata of path parameter.
   */
  export interface IParameter<
    Schema extends OpenApi.IJsonSchema = OpenApi.IJsonSchema,
  > {
    /**
     * Name of the path parameter.
     */
    name: string;

    /**
     * Key of the path parameter.
     */
